mapper.select:
select

from user_log where 1=1


	and creation_time >= #{startTime,jdbcType=BIGINT}



	and creation_time < #{endTime,jdbcType=BIGINT}



	and user_id in

		#{id}




	and category = #{category,jdbcType=VARCHAR}


order by creation_time desc
limit #{offset,jdbcType=INTEGER},
#{limit,jdbcType=INTEGER}

